import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

function CreateUser() {

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [passwordOne, setpasswordOne] = useState("");
    const [passwordTwo, setPasswordTwo] = useState("");
    const [emptyInput, setemptyInput] = useState("");
    const [responses, setResponses] = useState(false);
    useEffect(() => {



    }, [emptyInput]);


    const postUser = async (e) => {
        e.preventDefault();


        if (validateInputs() === true) {

            if (passwordOne === passwordTwo) {

                const newUser = { name, email, password: passwordOne }

                try {

                    const response = await axios.post("http://localhost:5000/user/register", newUser);
                    console.log(respose);
                    

                } catch (error) {
                    console.log(error);
                }

            } else {
                setemptyInput("Password does not match");
                setTimeout(() => {
                    setemptyInput("");
                }, 5000);

            }
        } else {

            console.log("error inputValidation");
        }
    }

    // true: no empty inputs, false: there must be one empty input.
    const validateInputs = () => {

        if (!name.trim()) {
            setemptyInput("Name is empty");
            setTimeout(() => {
                setemptyInput("");
            }, 5000);
        } else {

            if (!email.trim()) {
                setemptyInput("Email is empty");
                setTimeout(() => {
                    setemptyInput("");
                }, 5000);

            } else {

                if (!passwordOne.trim()) {
                    setemptyInput("Passwor is empty");
                    setTimeout(() => {
                        setemptyInput("");
                    }, 5000);
                } else {

                    if (!passwordTwo.trim()) {
                        setemptyInput("Password confirmation is empty");
                        setTimeout(() => {
                            setemptyInput("");
                        }, 5000);
                    } else {
                        return true;
                    }

                }

            }
        }
    }

    return (

        <div className="home-right-con">

            <div className="content-home-right ">

                <h1 className="bold-light">Take 10% off today.</h1>

                <form className="form-login" onSubmit={postUser} >

                    <label className="label-Inputs bold-light" htmlFor="email">Name and Lastname</label>
                    <input className="login-input" type="text" onChange={(e) => { setName(e.target.value) }} placeholder="your name" autoFocus />

                    <label className="label-Inputs bold-light mt-24" htmlFor="email">Email</label>
                    <input className="login-input" type="text" onChange={(e) => { setEmail(e.target.value) }} placeholder="Youremail@address.com" />

                    <div className="">

                        <label className="label-Inputs bold-light mt-24 r-label " htmlFor="email">Password</label>
                        <label className="label-Inputs bold-light mt-24 ml-40px" htmlFor="email">Password again</label>

                    </div>

                    <div className="display-flex">
                        <input onChange={(e) => { setpasswordOne(e.target.value) }} className="register-input " type="password" placeholder="Your password" required="" />
                        <input onChange={(e) => { setPasswordTwo(e.target.value) }} className="register-input  register-input-right" type="password" placeholder="Your password" />
                    </div>

                    <button type="submit" className="btn btn-primary w-100 btn-login">Register</button>
                    {
                        emptyInput ? (<span className="inputValidation-message">{emptyInput}</span>) : (<span></span>)
                    }
                </form>

                <div className="p-after-bttn">
                    <p>
                        <span className="textafterlogin">Already have an account </span>
                        <Link className="bold-light href" to="/user/login">Log In</Link>
                    </p>
                </div>
            </div>
        </div>

    );


}

export default CreateUser;
